PROCEDURE Proc_Statistic_GetOrgStatisticByDay (IN p_Date datetime)
BEGIN
  SET @p_Date = p_Date;

  CREATE TEMPORARY TABLE IF NOT EXISTS tbStatistic AS
  SELECT
    SUM(1) AS TotalInvoice,
    SUM(IFNULL(i.TotalAmount, 0)) AS TotalMoney,
    i.OrganizationId,
    @p_Date AS InvoiceDate
  FROM Ref i
  WHERE DATE(i.InvoiceDate) = DATE(@p_Date)
  GROUP BY i.OrganizationId;


  SELECT
    o.OrganizationId,
    o.OrganizationCode,
    o.OrganizationName,
    o.Email,
    IFNULL(s.TotalInvoice, 0) AS TotalInvoice,
    IFNULL(s.TotalMoney, 0) AS TotalMoney,
    p_Date AS InvoiceDate
  FROM Organization o
    LEFT JOIN tbStatistic s
      ON o.OrganizationId = s.OrganizationId;

  DROP TEMPORARY TABLE IF EXISTS tbStatistic;
END
$$

CREATE
DEFINER = 'nvmanh'@'%'
PROCEDURE Proc_Ref_GetNewRefNo (IN p_organizationId char(36), IN p_refType int)
BEGIN
  SET @currentDate = CURDATE();
  SET @day = CAST(DAY(@currentDate) AS int);
  SET @month = MONTH(@currentDate);
  SET @year = SUBSTRING(YEAR(@currentDate), 3);
  SET @dayString = '';


  IF (@day < 10) THEN
    SET @day = CONCAT('0', @day);
  END IF;

  IF (@month < 10) THEN
    SET @month = CONCAT('0', @month);
  END IF;


  SET @preFix = CONCAT(@year, @month, @day);

  SET @LAST_CODE = (SELECT
      MAX(r.RefNo)
    FROM Ref r
    WHERE RefNo LIKE CONCAT(@preFix, '%'));

  IF @LAST_CODE IS NULL THEN
    SELECT
      CONCAT(@preFix, '0001');
  ELSE
    SELECT
      CAST(@LAST_CODE AS int) + 1;
  END IF;
END
$$

