CREATE
DEFINER = 'nvmanh'@'%'
PROCEDURE Proc_GetOrganizationInfoByUserID (IN p_UserId char(36), IN p_OrganizationId varchar(255))
BEGIN
  SET @Month = MONTH(CURRENT_DATE());
  SET @Year = YEAR(CURRENT_DATE());

  -- Lấy thông tin doanh thu tháng hiện tại:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbInvoice AS (SELECT
      i.OrganizationId,
      COUNT(i.InvoiceId) AS TotalInvoices,
      SUM(IFNULL(i.TotalMoney, 0)) AS TotalMoneys
    FROM Invoice i
    WHERE OrganizationId = p_OrganizationId
    AND MONTH(i.InvoiceDate) = @Month
    AND YEAR(i.InvoiceDate) = @Year
    GROUP BY i.OrganizationId);

  SELECT
    o.*,
    IFNULL(i.TotalInvoices, 0) AS TotalInvoices,
    IFNULL(i.TotalMoneys, 0) AS TotalMoneys
  FROM Organization o
    LEFT JOIN tbInvoice i
      ON o.OrganizationId = i.OrganizationId
  WHERE o.OrganizationId = p_OrganizationId;

  DROP TEMPORARY TABLE IF EXISTS tbInvoice;
END
$$

