CREATE
DEFINER = 'nvmanh'@'%'
PROCEDURE Proc_Statistic_GetInvoiceByEmployee_V1 (IN p_StartDate datetime, IN p_EndDate datetime, IN p_OrganizationId char(36))
BEGIN

  -- Danh sách nhân viên theo đơn vị:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbEmployee AS
  SELECT
    EmployeeId,
    e.FullName
  FROM Employee e
  WHERE OrganizationId = p_OrganizationId;

  -- Danh sách hóa đơn:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbInvoice AS
  SELECT
    i.EmployeeId,
    i.InvoiceId,
    InvoiceDate,
    i.TotalMoney
  FROM Invoice i
    INNER JOIN tbEmployee e
      ON e.EmployeeId = i.EmployeeId
  WHERE OrganizationId = p_OrganizationId
  AND i.InvoiceDate BETWEEN p_StartDate AND p_EndDate;

  -- Chi tiết hóa đơn có trong khoảng thời gian:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbInvoiceDetail AS
  SELECT
    id.ProductId,
    id.TotalMoney,
    i.InvoiceDate,
    i.EmployeeId
  FROM InvoiceDetail id
    INNER JOIN tbInvoice i
      ON id.InvoiceId = i.InvoiceId;

  -- Danh mục sản phẩm được bán có trong hóa đơn (Kèm theo tổng tiền - nhóm theo nhân viên và từng sản phẩm):
  CREATE TEMPORARY TABLE IF NOT EXISTS tbTotalMoneyProducts AS
  SELECT
    t.EmployeeId,
    t.ProductId,
    p.GroupProductId,
    SUM(IFNULL(t.TotalMoney, 0)) AS TotalMoney
  FROM tbInvoiceDetail t
    INNER JOIN Product p
      ON t.ProductId = p.ProductId
  GROUP BY t.EmployeeId,
           t.ProductId,
           p.GroupProductId;

  -- Dữ liệu thống kê các nhóm sản phẩm theo từng nhân viên:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbTotalMoneyGroupProducts AS
  SELECT
    t.EmployeeId,
    g.GroupProductId,
    g.GroupProductName,
    SUM(t.TotalMoney) AS TotalMoneys
  FROM tbTotalMoneyProducts t
    LEFT JOIN GroupProduct g
      ON t.GroupProductId = g.GroupProductId
  GROUP BY t.EmployeeId,
           g.GroupProductId,
           g.GroupProductName;

  -- Kết quả cuối cùng:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbResult AS
  SELECT
    t.EmployeeId,
    t.FullName,
    y.GroupProductId,
    y.GroupProductName,
    SUM(IFNULL(y.TotalMoneys, 0)) AS TotalMoneys
  FROM tbEmployee t
    LEFT JOIN tbTotalMoneyGroupProducts y
      ON t.EmployeeId = y.EmployeeID
  GROUP BY t.EmployeeId,
           t.FullName,
           y.GroupProductId,
           y.GroupProductName
  ORDER BY TotalMoneys DESC;


  SELECT
    *
  FROM tbResult;

  DROP TEMPORARY TABLE IF EXISTS tbEmployee;
  DROP TEMPORARY TABLE IF EXISTS tbInvoice;
  DROP TEMPORARY TABLE IF EXISTS tbInvoiceDetail;
  DROP TEMPORARY TABLE IF EXISTS tbTotalMoneyProducts;
  DROP TEMPORARY TABLE IF EXISTS tbTotalMoneyGroupProducts;
  DROP TABLE IF EXISTS tbResult;
END
$$

CREATE
DEFINER = 'nvmanh'@'%'
PROCEDURE Proc_Invoice_GetPaging_V2 (IN p_organizationId char(36), IN p_limit int, IN p_offset int, IN p_textFilter varchar(255), OUT p_totalRecords int)
BEGIN
  CREATE TEMPORARY TABLE IF NOT EXISTS tbInvoice AS
  SELECT
    i.InvoiceId,
    i.InvoiceCode,
    i.InvoiceDate,
    i.InvoiceStatus,
    i.EmployeeId,
    i.CustomerId,
    i.CustomerName,
    i.CustomerAddress,
    i.TotalMoney,
    i.Description,
    i.OrganizationId,
    i.CreatedDate,
    i.CreatedBy,
    i.ModifiedDate,
    i.ModifiedBy,
    e.FirstName,
    e.LastName,
    e.FullName,
    CONCAT('[', GROUP_CONCAT(CONCAT('{"ProductName":"', p.ProductName, '","Total":', IFNULL(ide.TotalMoney, 0), '}') SEPARATOR ','), ']') AS ProductList
  FROM Invoice i
    LEFT JOIN Employee e
      ON i.EmployeeId = e.EmployeeId
    LEFT JOIN InvoiceDetail ide
      ON i.InvoiceId = ide.InvoiceId
    INNER JOIN Product p
      ON ide.ProductId = p.ProductId
  WHERE (i.OrganizationId = p_organizationId)
  AND (i.InvoiceCode LIKE CONCAT('%', IFNULL(p_textFilter, ''), '%')
  OR e.FullName LIKE CONCAT('%', IFNULL(p_textFilter, ''), '%'))
  GROUP BY i.InvoiceId,
           i.InvoiceCode,
           i.InvoiceDate,
           i.InvoiceStatus,
           i.EmployeeId,
           i.CustomerId,
           i.CustomerName,
           i.CustomerAddress,
           i.TotalMoney,
           i.Description,
           i.OrganizationId,
           i.CreatedDate,
           i.CreatedBy,
           i.ModifiedDate,
           i.ModifiedBy,
           e.FirstName,
           e.LastName,
           e.FullName
  ORDER BY i.CreatedDate DESC;

  SET p_totalRecords = (SELECT
      COUNT(*)
    FROM tbInvoice);
  SELECT
    *
  FROM tbInvoice i LIMIT p_limit OFFSET p_offset;

  DROP TEMPORARY TABLE IF EXISTS tbInvoice;
END
$$

