CREATE
DEFINER = 'nvmanh'@'%'
PROCEDURE Proc_Statistic_GetInvoiceByEmployee (IN p_StartDate datetime, IN p_EndDate datetime, IN p_OrganizationId char(36), IN p_categoryId char(36))
BEGIN

  -- Danh sách nhân viên theo đơn vị:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbEmployee AS
  SELECT
    EmployeeId,
    e.FullName
  FROM Employee e
  WHERE OrganizationId = p_OrganizationId;

  -- Danh sách hóa đơn:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbInvoice AS
  SELECT
    i.EmployeeId,
    i.RefId,
    i.RefDate,
    i.TotalAmount
  FROM Ref i
    INNER JOIN tbEmployee e
      ON e.EmployeeId = i.EmployeeId
  WHERE OrganizationId = p_OrganizationId
  AND i.RefDate BETWEEN p_StartDate AND p_EndDate;

  -- Chi tiết hóa đơn có trong khoảng thời gian:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbInvoiceDetail AS
  SELECT
    id.InventoryItemId,
    id.TotalAmount,
    i.RefDate,
    i.EmployeeId
  FROM RefDetail id
    INNER JOIN tbInvoice i
      ON id.RefId = i.RefId;

  -- Danh mục sản phẩm được bán có trong hóa đơn (Kèm theo tổng tiền - nhóm theo nhân viên và từng sản phẩm):
  CREATE TEMPORARY TABLE IF NOT EXISTS tbTotalMoneyProducts AS
  SELECT
    t.EmployeeId,
    t.InventoryItemId,
    p.InventoryItemCategoryId,
    SUM(IFNULL(t.TotalAmount, 0)) AS TotalAmount
  FROM tbInvoiceDetail t
    INNER JOIN InventoryItem p
      ON t.InventoryItemId = p.InventoryItemId
  WHERE (p_categoryId IS NULL
  OR (p_categoryId IS NOT NULL
  AND p.InventoryItemCategoryId = p_categoryId))
  GROUP BY t.EmployeeId,
           t.InventoryItemId,
           p.InventoryItemCategoryId;

  -- Dữ liệu thống kê các nhóm sản phẩm theo từng nhân viên:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbTotalMoneyGroupProducts AS
  SELECT
    t.EmployeeId,
    g.InventoryItemCategoryId,
    g.InventoryItemCategoryName,
    SUM(t.TotalAmount) AS TotalAmount
  FROM tbTotalMoneyProducts t
    LEFT JOIN InventoryItemCategory g
      ON t.InventoryItemCategoryId = g.InventoryItemCategoryId
  GROUP BY t.EmployeeId,
           g.InventoryItemCategoryId,
           g.InventoryItemCategoryName;

  -- Kết quả cuối cùng:
  CREATE TEMPORARY TABLE IF NOT EXISTS tbResult AS
  SELECT
    t.EmployeeId,
    t.FullName,
    y.InventoryItemCategoryId,
    y.InventoryItemCategoryName,
    SUM(IFNULL(y.TotalAmount, 0)) AS TotalAmount
  FROM tbEmployee t
    LEFT JOIN tbTotalMoneyGroupProducts y
      ON t.EmployeeId = y.EmployeeID
  GROUP BY t.EmployeeId,
           t.FullName,
           y.InventoryItemCategoryId,
           y.InventoryItemCategoryName
  ORDER BY TotalAmount DESC;


  SELECT
    *
  FROM tbResult;

  DROP TEMPORARY TABLE IF EXISTS tbEmployee;
  DROP TEMPORARY TABLE IF EXISTS tbInvoice;
  DROP TEMPORARY TABLE IF EXISTS tbInvoiceDetail;
  DROP TEMPORARY TABLE IF EXISTS tbTotalMoneyProducts;
  DROP TEMPORARY TABLE IF EXISTS tbTotalMoneyGroupProducts;
  DROP TABLE IF EXISTS tbResult;
END
$$

CREATE
DEFINER = 'nvmanh'@'%'
PROCEDURE Proc_Ref_GetPaging (IN p_organizationId char(36), IN p_limit int, IN p_offset int, IN p_textFilter varchar(255), OUT p_totalRecords int)
BEGIN
  CREATE TEMPORARY TABLE IF NOT EXISTS tbRef AS
  SELECT
    i.RefId,
    i.RefNo,
    i.RefDate,
    i.RefType,
    i.PaymentStatus,
    i.EmployeeId,
    i.CustomerId,
    i.CustomerName,
    i.CustomerAddress,
    i.TotalAmount,
    i.OrganizationId,
    i.CreatedDate,
    i.CreatedBy,
    i.ModifiedDate,
    i.ModifiedBy,
    e.FirstName,
    e.LastName,
    e.FullName,
    CONCAT('[', GROUP_CONCAT(CONCAT('{"InventoryItemName":"', p.InventoryItemName, '","Quantity":', ide.Quantity, ',"UnitPrice":', ide.UnitPrice, ',"Total":', IFNULL(ide.TotalAmount, 0), '}') SEPARATOR ','), ']') AS InventoryItemList
  FROM Ref i
    LEFT JOIN Employee e
      ON i.EmployeeId = e.EmployeeId
    LEFT JOIN RefDetail ide
      ON i.RefId = ide.RefId
    INNER JOIN InventoryItem p
      ON ide.InventoryItemId = p.InventoryItemId
  WHERE (i.OrganizationId = p_organizationId)
  AND (i.RefNo LIKE CONCAT('%', IFNULL(p_textFilter, ''), '%')
  OR e.FullName LIKE CONCAT('%', IFNULL(p_textFilter, ''), '%'))
  GROUP BY i.RefId,
           i.RefNo,
           i.RefDate,
           i.RefType,
           i.PaymentStatus,
           i.EmployeeId,
           i.CustomerId,
           i.CustomerName,
           i.CustomerAddress,
           i.TotalAmount,
           i.OrganizationId,
           i.CreatedDate,
           i.CreatedBy,
           i.ModifiedDate,
           i.ModifiedBy,
           e.FirstName,
           e.LastName,
           e.FullName
  ORDER BY i.CreatedDate DESC;

  SET p_totalRecords = (SELECT
      COUNT(*)
    FROM tbRef);
  SELECT
    *
  FROM tbRef i LIMIT p_limit OFFSET p_offset;

  DROP TEMPORARY TABLE IF EXISTS tbRef;
END
$$

CREATE
DEFINER = 'nvmanh'@'%'
PROCEDURE Proc_GetProfitAmountByItems (IN p_startDate datetime, IN p_endDate datetime, IN p_organizationId char(36), IN p_categoryId char(36))
BEGIN

  DROP TEMPORARY TABLE IF EXISTS tbInventoryItem;
  DROP TEMPORARY TABLE IF EXISTS tbRefDetail;
  DROP TEMPORARY TABLE IF EXISTS tbCalculatorDetail;
  DROP TEMPORARY TABLE IF EXISTS tbProfitByItem;
  DROP TEMPORARY TABLE IF EXISTS tbResult;

  CREATE TEMPORARY TABLE tbInventoryItem AS
  SELECT
    ii.InventoryItemId,
    ii.InventoryItemCode,
    ii.InventoryItemName,
    ii.CostPrice,
    ii.UnitPrice,
    ii.InventoryItemCategoryId
  FROM InventoryItem ii
  WHERE (p_categoryId IS NOT NULL
  AND ii.InventoryItemCategoryId = p_categoryId)
  OR p_categoryId IS NULL;

  CREATE TEMPORARY TABLE tbRefDetail AS
  SELECT
    rd.InventoryItemId,
    rd.Quantity,
    rd.CostPrice,
    rd.UnitPrice,
    rd.TotalAmount
  FROM RefDetail rd
    INNER JOIN Ref r
      ON rd.RefId = r.RefId
  WHERE r.RefDate BETWEEN IFNULL(p_startDate, TIMESTAMP('1900-01-01 00:00:00')) AND IFNULL(p_endDate, CURRENT_TIMESTAMP());


  CREATE TEMPORARY TABLE tbCalculatorDetail AS
  SELECT
    ROW_NUMBER() OVER (PARTITION BY ii.InventoryItemId) AS GroupSort,
    ii.InventoryItemId,
    ii.InventoryItemCode,
    ii.InventoryItemName,
    ii.CostPrice,
    ii.UnitPrice,
    ii.InventoryItemCategoryId,
    rd.Quantity,
    IFNULL(rd.CostPrice, 0) * IFNULL(rd.Quantity, 0) AS TotalCostPrice,
    IFNULL(rd.UnitPrice, 0) * IFNULL(rd.Quantity, 0) AS TotalUnitPrice,
    rd.TotalAmount
  FROM tbInventoryItem ii
    LEFT JOIN tbRefDetail rd
      ON ii.InventoryItemId = rd.InventoryItemId;


  CREATE TEMPORARY TABLE tbProfitByItem AS
  SELECT
    c.InventoryItemId,
    c.InventoryItemCode,
    c.InventoryItemName,
    c.InventoryItemCategoryId,
    SUM(IFNULL(c.Quantity, 0)) AS TotalQuantity,
    SUM(IFNULL(c.TotalCostPrice, 0)) AS TotalCostPrice,
    SUM(IFNULL(c.TotalUnitPrice, 0)) AS TotalUnitPrice,
    SUM(IFNULL(c.TotalAmount, 0)) AS TotalAmount
  FROM tbCalculatorDetail c
  GROUP BY c.InventoryItemId,
           c.InventoryItemCode,
           c.InventoryItemName,
           c.InventoryItemCategoryId;

  CREATE TEMPORARY TABLE tbResult AS
  SELECT
    t1.InventoryItemId,
    t1.InventoryItemCode,
    t1.InventoryItemName,
    t1.InventoryItemCategoryId,
    IFNULL(t2.InventoryItemCategoryName, '(Chưa phân nhóm)') AS InventoryItemCategoryName,
    t1.TotalQuantity,
    t1.TotalCostPrice,
    t1.TotalUnitPrice,
    t1.TotalAmount,
    (t1.TotalUnitPrice - t1.TotalCostPrice) AS TotalProfitAmount
  FROM tbProfitByItem t1
    LEFT JOIN InventoryItemCategory t2
      ON t1.InventoryItemCategoryId = t2.InventoryItemCategoryId;

  SELECT
    *
  FROM tbResult r
  ORDER BY r.TotalProfitAmount DESC;

  DROP TEMPORARY TABLE IF EXISTS tbInventoryItem;
  DROP TEMPORARY TABLE IF EXISTS tbRefDetail;
  DROP TEMPORARY TABLE IF EXISTS tbCalculatorDetail;
  DROP TEMPORARY TABLE IF EXISTS tbProfitByItem;
  DROP TEMPORARY TABLE IF EXISTS tbResult;
END
$$

