DEFINER = 'nvmanh'@'%'
FUNCTION IF NOT EXISTS Func_GetPrefixNewCode (tableName varchar(255))
RETURNS varchar(50) CHARSET utf8
BEGIN
  DECLARE Prefix varchar(50);
  SET Prefix = tableName;
  SET Prefix = (SELECT
      CASE WHEN tableName = 'Employee' THEN 'NV' WHEN tableName = 'CustomerGroup' THEN 'NKH' WHEN tableName = 'EmployeePosition' THEN 'VT' WHEN tableName = 'GroupProduct' THEN 'NSP' WHEN tableName = 'Invoice' THEN 'HD' WHEN tableName = 'MSLicense' THEN 'LS' WHEN tableName = 'Organization' THEN 'DN' WHEN tableName = 'Unit' THEN 'DV' WHEN tableName = 'Service' THEN 'DV' WHEN tableName = 'ServiceGroup' THEN 'NDV' ELSE 'MS' END);
  RETURN Prefix;
