DELIMITER $$

CREATE
DEFINER = 'nvmanh'@'%'
TRIGGER IF NOT EXISTS TG_RefBeforeInsert
BEFORE INSERT
ON Ref
FOR EACH ROW
BEGIN
  SET @currentDate = CURDATE();
  SET @day = CAST(DAY(@currentDate) AS int);
  SET @month = MONTH(@currentDate);
  SET @year = SUBSTRING(YEAR(@currentDate), 3);
  SET @dayString = '';


  IF (@day < 10) THEN
    SET @day = CONCAT('0', @day);
  END IF;

  IF (@month < 10) THEN
    SET @month = CONCAT('0', @month);
  END IF;


  SET @preFix = CONCAT(@year, @month, @day);

  SET @LAST_CODE = (SELECT
      MAX(r.RefNo)
    FROM Ref r
    WHERE RefNo LIKE CONCAT(@preFix, '%')
    AND OrganizationId = NEW.OrganizationId);

  IF @LAST_CODE IS NULL THEN
    SET NEW.RefNo =
    CONCAT(@preFix, '0001');
  ELSE
    SET NEW.RefNo = (CAST(@LAST_CODE AS int) + 1);
  END IF;
END
$$

CREATE
DEFINER = 'nvmanh'@'%'
TRIGGER IF NOT EXISTS TG_InvoiceDetail_BeforInsert
BEFORE INSERT
ON InvoiceDetail
FOR EACH ROW
BEGIN
  IF NEW.InvoiceDetailId IS NULL
    OR NEW.InvoiceDetailId = ''
    OR NEW.InvoiceDetailId = '00000000-0000-0000-0000-000000000000' THEN
    SET NEW.InvoiceDetailId = UUID();
  END IF;
  IF NEW.CreatedDate IS NULL THEN
    SET NEW.CreatedDate = CURRENT_TIMESTAMP();
  END IF;
END
$$

CREATE
DEFINER = 'nvmanh'@'%'
TRIGGER IF NOT EXISTS TG_InventoryItem_BeforeInsert
BEFORE INSERT
ON InventoryItem
FOR EACH ROW
BEGIN
  SET NEW.CreatedDate = CURRENT_TIMESTAMP();
END
$$

CREATE
DEFINER = 'nvmanh'@'%'
TRIGGER IF NOT EXISTS TG_Employee_BeforeInsert
BEFORE INSERT
ON Employee
FOR EACH ROW
BEGIN
  IF NEW.EmployeeId IS NULL
    OR NEW.EmployeeId = '' THEN
    SET NEW.EmployeeId = UUID();
  END IF;
  IF NEW.CreatedDate IS NULL THEN
    SET NEW.CreatedDate = CURRENT_TIMESTAMP();
  END IF;
END
$$

